mapper.update:
update setmeal
category_id=#{categoryId}, name=#{name}, price=#{price}, status=#{status}, description=#{description}, image=#{image}, update_time=#{updateTime}, update_user=#{updateUser},
where id=#{id}

mapper.select:
select *,c.name categoryName from setmeal left join category c  on c.id = setmeal.category_id


        and name like concat('%',#{name},'%')


        and category_id=#{categoryId}


        and status=#{status}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
